<?php

namespace Test;
use PHPUnit\Framework\TestCase;
use Numbers\Numbers;
use Tiers\Tiers;

class Test01Test extends TestCase
{
    public function test01 () {
        $expected = "";
        $result = (new Tiers())->apply("");
        $this->assertEquals($result, $expected );
    }

    public function test02 () {
        $expected = "*\n";
        $result = (new Tiers())->apply(1);
        $this->assertEquals($result, $expected );
    }

